var execQueue = function(){
	var queue = [],
		hasStarted = false;
	
	this.push = function(){
		var args = [];
		
		for(var i = arguments.length - 1; i + 1; i--){
			args.unshift(arguments[i]);
		}
		
		queue.push(args);
		
		return this;
	};
	
	this.next = function(){
		if(queue.length){
			var fna = queue.shift();
			
			fna.shift().apply({}, fna);
		}
	};
	
	this.exec = function(){
		if(hasStarted){
			return false;
		}
		
		this.next();
		
		hasStarted = true;
		
		return true;
	};
	
	this.abort = function(){
		queue = [];
	};
	
	this.__defineGetter__("length", function(){
		return queue.length;
	});
};

exports.execQueue = execQueue;
